function modifyInput(a){$.each(a,function(d,f){var b=$("#"+f+" input");var e=$("#"+f+" label");for(var c=0;c<=b.length;c++){if(b.eq(c).val()!=""){b.eq(c).prev("label").animate({opacity:"0.4",marginLeft:"9em"},"normal")}}e.click(function(){$(this).next("input").focus()});b.focus(function(){if($(this).val()!=""){$(this).prev("label").animate({opacity:"0.4",marginLeft:"9em"},"normal")}else{$(this).prev("label").animate({opacity:"0.4"},"normal")}}).keyup(function(){var g=$(this).prev("label");if($(this).val()==""){g.animate({opacity:"0.4",marginLeft:"0px"},"fast")}else{g.animate({opacity:"0",marginLeft:"9em"},"normal")}}).blur(function(){if($(this).val()==""){$(this).prev().animate({opacity:"1",marginLeft:"0px"},"normal")}})})};
