$(function(){$(".update").click(function(){if($(".update").attr("data")=="y"){$(".update").attr("data","n");$(".update_pass").slideDown(1000);$(".update").text("\u93c0\u60e7\u7d14\u6dc7\ue1bd\u657c");$(".yes_update").show()}else{$(".update").attr("data","y");$(".update_pass").slideUp(1000);$(".update").text("\u6dc7\ue1bd\u657c\u7035\u55d9\u721c");$(".yes_update").hide()}});$(".td_right").dblclick(function(){if($(this).find("input").val()!=undefined){}else{if($(this).attr("data")=="sex"){$(".select").fadeIn(200);$(".select").mouseleave(function(){$(".select").fadeOut(200)});choice_sex(".sex_m","\u9422\ufffd");choice_sex(".sex_w","\u6fc2\ufffd")}else{var a="<input  class='height_30'type='text' value='"+$(this).text()+"'>";$(this).html(a);$(this).find("input").focus();$(this).find("input").change(function(){});$(this).find("input").blur(function(){$(this).parent().html("<span>"+$(this).val()+"</span>")})}}})});function choice_sex(b,a){$(b).click(function(){$(".sex").text(a);$(".select").fadeOut(200)})};
