function showDownLoadCode(a){window.open("/download/ocx/Active.exe")}function hideDown(a){$(".OcxDown").slideUp(300);$("#errorMessage").text("")}function download_file(){var a=$("#verificationCode3").val();$.ajax({type:"POST",url:"download_check",data:{verificationCode3:a},success:function(c){var b=$.parseJSON(c);if(b.status==1200){$("#errorMessage").text("");$("#ocxDownload input[name=checkCode]").val(b.message);$("#ocxDownload").submit();hideDown(this)}else{$("#errorMessage").text(b.message)}}})};
